import React from "react";

const CardHeaderTabs = ({ tabs, activeTab, onTabClick }) => {
  return (
    <ul className="nav nav-tabs  card-header-tabs nav-fill">
      {tabs.map((tab, index) => (
        <li
          key={tab.id}
          id={tab.id}
          className="nav-item"
          onClick={() => onTabClick(tab)}
        >
          <span
            className={
              activeTab === tab
                ? "badge badge-pill bg-secondary"
                : "badge badge-pill bg-primary"
            }
            style={{ color: "whitesmoke", margin: "10px" }}
          >
            Step {index + 1}{" "}
          </span>
          <span className={activeTab === tab ? "nav-link active" : "nav-link"}>
            {tab.title}
          </span>
        </li>
      ))}
    </ul>
  );
};

export default CardHeaderTabs;
