import React, { Component } from "react";

class CalculateFormPartial extends Component {
  state = {};

  render() {
    const { onServiceTypeChange, serviceType, onSubmit } = this.props;

    return (
      <React.Fragment>
        <h3 className="text-secondary">Calculate</h3>
        <div className="row">
          <div className="col-sm-12">
            <h5>iWealth Service Type</h5>
            <p className="text-muted">
              iWealth offers investors differnt levels of assistance during the
              tax lien process.
            </p>
          </div>
          <div className="col-sm-12">
            <div className="form-check">
              <input
                className="form-check-input"
                type="radio"
                name="serviceTypeOptions"
                id="serviceType.setup"
                value="setup"
                checked={serviceType.setup}
                onChange={onServiceTypeChange}
              />
              <label className="form-check-label" htmlFor="serviceType.setup">
                <span className="service-type-title">Setup: </span>{" "}
                <span className="text-muted">
                  Help with first investment cycle only.
                </span>
              </label>
            </div>
            <div className="form-check">
              <input
                className="form-check-input"
                type="radio"
                name="serviceTypeOptions"
                id="serviceType.recurring"
                value="recurring"
                checked={serviceType.recurring}
                onChange={onServiceTypeChange}
              />
              <label
                className="form-check-label"
                htmlFor="serviceType.recurring"
              >
                <span className="service-type-title">Recurring: </span>
                <span className="text-muted">
                  Help during all investment cycle.
                </span>
              </label>
            </div>
            <div className="form-check">
              <input
                className="form-check-input"
                type="radio"
                name="serviceTypeOptions"
                id="serviceType.fullyManaged"
                value="fullyManaged"
                checked={serviceType.fullyManaged}
                onChange={onServiceTypeChange}
              />
              <label
                className="form-check-label"
                htmlFor="serviceType.fullyManaged"
              >
                <span className="service-type-title">Fully Managed: </span>
                <span className="text-muted">
                  Autopilot for your investments.
                </span>
              </label>
            </div>
            <button type="submit" className="btn btn-primary btn-block mt-5">
              Calculate
            </button>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default CalculateFormPartial;

// *****************************
// Deal Information
// *****************************
// Fee Structure
// --Setup: One-time (Up to 20 liens can purchase for $10,000)
// --Fully Managed: Ongoing (2nd time forward get 10% discount)
// Non Redemption Ratio (e.g. 1 in 6 non-redemptions) --Done
// --Need at least this many liens (need this much cash = avg lien cost * ratio + fee)
// Avg Net Rent (Caculated Field Gross minus costs) -- Done
// --Gross Rent
// --Pct Management (10%)
// --Pct Vacancy
// --Pct Repair
