import React, { Component } from "react";
import CardHeaderTabs from "../../common/cardHeaderTabs";
import InvestorFormPartial from "../investorFormPartial/investorFormPartial";
import RenoFormPartial from "../renoFormPartial/renoFormPartial";
import DealFormPartial from "./../dealFormPartial/dealFormPartial";
import CalculateFormPartial from "./../calculateFormPartial/calculateFormPartial";

class InputForm extends Component {
  state = { activeTab: {} };

  tabs = [
    { id: "inventoryTab", title: "Investor" },
    { id: "dealTab", title: "Deal" },
    { id: "renoTab", title: "Reno" },
    { id: "calculateTab", title: "Calculate" }
  ];

  componentDidMount() {
    this.setState({ activeTab: this.tabs[0] });
  }

  handleTabClick = tab => {
    const activeTabIndex = this.tabs.indexOf(tab);
    this.setState({ activeTab: this.tabs[activeTabIndex] });
  };

  render() {
    const { activeTab } = this.state;
    const {
      investorInformation,
      dealInformation,
      serviceType,
      onChange,
      onNetRentPreferenceChange,
      onLocationChange,
      onLienCostChange,
      onSecondaryMarketChange,
      onRedemptionRatioChange,
      onExpenseChange,
      onGrossRentChange,
      onServiceTypeChange,
      onSubmit
    } = this.props;
    return (
      <div className="card">
        <div className="card-header bg-default">
          <CardHeaderTabs
            tabs={this.tabs}
            activeTab={activeTab}
            onTabClick={this.handleTabClick}
          />
        </div>
        <div className="card-body">
          <form onSubmit={onSubmit}>
            {/* Tab 1 */}
            {activeTab.id === "inventoryTab" && (
              <InvestorFormPartial
                investorInformation={investorInformation}
                onChange={onChange}
                onNetRentPreferenceChange={onNetRentPreferenceChange}
                onLocationChange={onLocationChange}
                onLienCostChange={onLienCostChange}
                onSecondaryMarketChange={onSecondaryMarketChange}
              />
            )}

            {/* Tab 2 */}
            {activeTab.id === "dealTab" && (
              <DealFormPartial
                dealInformation={dealInformation}
                onRedemptionRatioChange={onRedemptionRatioChange}
                onExpenseChange={onExpenseChange}
                onGrossRentChange={onGrossRentChange}
                onServiceTypeChange={onServiceTypeChange}
              />
            )}

            {/* Tab 3 */}
            {activeTab.id === "renoTab" && <RenoFormPartial />}

            {/* Tab 4 */}
            {activeTab.id === "calculateTab" && (
              <CalculateFormPartial
                serviceType={serviceType}
                onServiceTypeChange={onServiceTypeChange}
              />
            )}
          </form>
        </div>
      </div>
    );
  }
}

export default InputForm;
