import React, { Component } from "react";
import queryString from "query-string";
import InputForm from "../inputForm/inputForm";
import ResultsTable from "../resultsTable/resultsTable";
import { getDefaults } from "./../../services/fakeDefaultService";
import Investor from "../../models/investor";
import { getActiveStates } from "../../services/fakeStateService";
import _ from "lodash";

class Home extends Component {
  state = {
    investorInformation: {
      secondaryMarket: false,
      startDate: "",
      passiveIncomeGoal: "",
      investmentCapital: "",
      netRentBreakdown: {
        paydownDebtPct: 0,
        reinvestPct: 0,
        spendPct: 0
      },
      locations: [],
      lienCosts: [],
      lienInterestRates: []
    },
    dealInformation: {
      redemptionRatio: 0,
      avgGrossRent: 0,
      expenses: {
        management: 0,
        vacancy: 0,
        repair: 0
      }
    },
    renoInformation: {
      avgRenoDuration: 0,
      avgRenoCost: 0,
      loanRate: 0,
      loanTerm: 0
    },
    serviceType: {
      setup: false,
      recurring: false
    },
    periods: [],
    errors: {
      investorForm: {},
      renoForm: {}
    }
  };

  componentDidMount() {
    // Get Defaults
    const {
      investorInformation,
      dealInformation,
      renoInformation,
      serviceType
    } = getDefaults();

    // Set investment start date to next month by default.
    let today = new Date();
    let startDate = new Date(today);
    startDate.setUTCMonth(startDate.getUTCMonth() + 1);
    startDate.setUTCDate(1);
    investorInformation.startDate = startDate;

    this.setState({
      investorInformation,
      dealInformation,
      renoInformation,
      serviceType
    });
  }

  validatePoperty = input => {};

  handleNumberInputChange = ({ currentTarget: input }) => {
    const parentObjName = input.name.split(".")[0];
    const path = input.name.replace(parentObjName + ".", "");

    const objCopy = { ...this.state[parentObjName] };
    _.set(objCopy, path, input.value);
    this.setState({ [parentObjName]: objCopy });
  };

  handleDateChange = startDate => {
    const investorInformation = { ...this.state.investorInformation };
    investorInformation.startDate = startDate;
    this.setState({ investorInformation });
  };

  handleNetRentPreferenceChange = ({ currentTarget: input }) => {
    const investorInformation = { ...this.state.investorInformation };
    const netRentBreakdown = {
      ...this.state.investorInformation.netRentBreakdown
    };

    let value = parseInt(input.value);

    if (!value) {
      netRentBreakdown[input.name] = "";
      let delta =
        0 - this.state.investorInformation.netRentBreakdown[input.name];
      let deltas =
        delta % 2 !== 0
          ? [Math.floor(delta / 2), Math.ceil(delta / 2)]
          : [delta / 2, delta / 2];

      switch (input.name) {
        case "spendPct":
          netRentBreakdown.reinvestPct -=
            Math.ceil(
              delta *
                (netRentBreakdown.reinvestPct /
                  (netRentBreakdown.reinvestPct +
                    netRentBreakdown.paydownDebtPct))
            ) || deltas[0];
          netRentBreakdown.paydownDebtPct =
            100 - (0 + netRentBreakdown.reinvestPct);
          break;
        case "paydownDebtPct":
          netRentBreakdown.reinvestPct -= delta;
          break;
        case "reinvestPct":
          netRentBreakdown.paydownDebtPct -= delta;
          break;
        default:
          netRentBreakdown.spendPct = 10;
          netRentBreakdown.paydownDebtPct = 90;
          netRentBreakdown.reinvestPct = 0;
          break;
      }
      investorInformation.netRentBreakdown = netRentBreakdown;
      this.setState({ investorInformation });
      return;
    }

    // If user inputs 100, zero out other two (let them override)
    if (value === 100) {
      netRentBreakdown[input.name] = 100;
      for (let key in netRentBreakdown) {
        if (key !== input.name) {
          netRentBreakdown[key] = 0;
        }
      }
      investorInformation.netRentBreakdown = netRentBreakdown;
      this.setState({ investorInformation });
      return;
    }

    // Don't let user enter a value that would result in
    // a total greather than 100%
    if (input.name !== "spendPct" && value > 100 - netRentBreakdown.spendPct) {
      value = 100 - netRentBreakdown.spendPct;
    } else if (input.name === "spendPct" && value > 100) {
      value = 100;
    }

    // Don't let a user enter a negative value
    if (value < 0) value = 0;

    netRentBreakdown[input.name] = value;

    let delta =
      value - this.state.investorInformation.netRentBreakdown[input.name];
    let deltas =
      delta % 2 !== 0
        ? [Math.floor(delta / 2), Math.ceil(delta / 2)]
        : [delta / 2, delta / 2];

    switch (input.name) {
      case "spendPct":
        netRentBreakdown.reinvestPct -=
          Math.ceil(
            delta *
              (netRentBreakdown.reinvestPct /
                (netRentBreakdown.reinvestPct +
                  netRentBreakdown.paydownDebtPct))
          ) || deltas[0];
        netRentBreakdown.paydownDebtPct =
          100 - (Math.abs(value) + netRentBreakdown.reinvestPct);
        break;
      case "paydownDebtPct":
        netRentBreakdown.reinvestPct -= delta;
        break;
      case "reinvestPct":
        netRentBreakdown.paydownDebtPct -= delta;
        break;
      default:
        netRentBreakdown.spendPct = 10;
        netRentBreakdown.paydownDebtPct = 90;
        netRentBreakdown.reinvestPct = 0;
        break;
    }
    investorInformation.netRentBreakdown = netRentBreakdown;
    this.setState({ investorInformation });
  };

  handleLocationChange = ({ currentTarget: input }) => {
    const investorInformation = { ...this.state.investorInformation };
    const activeStates = getActiveStates();
    const newState = activeStates.find(
      location => location.abbreviation === input.value
    );
    const changedIndex = input.name.split("-")[1];

    investorInformation.locations[changedIndex] = newState;
    investorInformation.lienCosts[changedIndex] = {
      avgLienCostLiveAuction: newState.avgLienCostLiveAuction,
      avgLienCostSecondary: newState.avgLienCostSecondary
    };
    investorInformation.lienInterestRates[changedIndex] =
      newState.avgLienInterestRate;

    this.setState({ investorInformation });
  };

  handleLienCostChange = ({ currentTarget: input }) => {
    const investorInformation = { ...this.state.investorInformation };
    const changedIndex = input.name.split("-")[1];
    const changedField = input.name.split("-")[0];

    if (input.value < 0) {
      input.value = 0;
    }

    investorInformation.lienCosts[changedIndex][changedField] =
      parseInt(input.value.replace(/\b0+/g, "")) || 0;
    this.setState({ investorInformation });
  };

  handleLienInterestChange = ({ currentTarget: input }) => {
    const investorInformation = { ...this.state.investorInformation };
    const changedIndex = input.name.split("-")[1];

    if (input.value < 0) {
      input.value = 0;
    }

    console.log(parseInt(input.value.replace(/\b0+/g, "")) || 0);
    investorInformation.lienInterestRates[changedIndex] =
      parseInt(input.value.replace(/\b0+/g, "")) || 0;

    this.setState({ investorInformation });
  };

  handleSecondaryMarketChange = ({ currentTarget: input }) => {
    const investorInformation = { ...this.state.investorInformation };
    investorInformation.secondaryMarket = input.checked;
    this.setState({ investorInformation });
  };

  handleSliderChange = (value, objName, path) => {
    let objCopy = { ...this.state[objName] };
    _.set(objCopy, path, value);
    this.setState({ [objName]: objCopy });
  };

  handleServiceTypeChange = ({ currentTarget: input }) => {
    let serviceType = { ...this.state.serviceType };

    for (let key in serviceType) {
      serviceType[key] = false;
    }

    serviceType[input.value] = true;
    this.setState({ serviceType });
  };

  validate = () => {
    return { passiveIncomeGoal: "You are asking for too little." };
  };

  handleSubmit = e => {
    e.preventDefault();

    const errors = this.validate();
    this.setState({ errors: { investorForm: errors } });
    if (errors) return;

    //create investor & simulate process.
    const investor = new Investor(this.state);
    investor.simulate().then(periods => {
      this.setState({ periods });
    });
  };

  render() {
    const {
      errors,
      investorInformation,
      dealInformation,
      renoInformation,
      serviceType
    } = this.state;
    const { location } = this.props;
    const optionalParams = queryString.parse(location.search);

    return (
      <React.Fragment>
        <div>
          <InputForm
            errors={errors}
            investorInformation={investorInformation}
            dealInformation={dealInformation}
            renoInformation={renoInformation}
            serviceType={serviceType}
            onDateChange={this.handleDateChange}
            onNumberInputChange={this.handleNumberInputChange}
            onSliderChange={this.handleSliderChange}
            onNetRentPreferenceChange={this.handleNetRentPreferenceChange}
            onLocationChange={this.handleLocationChange}
            onLienCostChange={this.handleLienCostChange}
            onLienInterestChange={this.handleLienInterestChange}
            onSecondaryMarketChange={this.handleSecondaryMarketChange}
            onServiceTypeChange={this.handleServiceTypeChange}
            onSubmit={this.handleSubmit}
          />

          {/* <p>{JSON.stringify(optionalParams)}</p> */}
        </div>
        <div style={{ marginTop: "50px" }}>
          <ResultsTable
            periods={this.state.periods}
            investorInformation={investorInformation}
          />
        </div>
      </React.Fragment>
    );
  }
}

export default Home;

// Outputs
// Should be a schedule that can be viewed as years (aggregated values) or months
// Should last 24 Months after Investment Goal is hit
// PHase 2 Consideration --- people can pick fields to add to output.

