import React, { Component } from "react";
import "./calculateFormPartial.css";

class CalculateFormPartial extends Component {
  state = {};

  renderSecondaryMarket() {
    return (
      <React.Fragment>
        <h5>iWealth Secondary Market</h5>
        <p className="text-muted">
          iWealth offers investors access to liens whoes redemption periods are
          nearing end through a secondary market. These liens can significantly
          reduce the time it takes to go from a tax lien to a passive income
          producing house. Additionally, these liens typically have a higher
          likihood of becomming a house. Investors can access the secondary
          market by paying a one-time fee of $10,000. Following that investors
          pay iWealth a 10% for the total cost of liens purchased durning an
          investment cycle.
        </p>
      </React.Fragment>
    );
  }

  renderLiveAuctionText() {
    const { onServiceTypeChange, serviceType } = this.props;

    return (
      <div className="row">
        <div className="col-sm-12">
          <h5>iWealth Service Type</h5>
          <p className="text-muted">
            iWealth offers investors different levels of assistance during the
            tax lien process.
          </p>
        </div>
        <div className="col-sm-12">
          <div className="form-check">
            <input
              className="form-check-input"
              type="radio"
              name="serviceTypeOptions"
              id="serviceType.setup"
              value="setup"
              checked={serviceType.setup}
              onChange={onServiceTypeChange}
            />
            <label className="form-check-label" htmlFor="serviceType.setup">
              <span className="service-type-title">Setup: </span>{" "}
              <span className="text-muted">
                Help with first investment cycle only. $10,000 service fee per
                investment cycle.
              </span>
            </label>
          </div>
          <div className="form-check">
            <input
              className="form-check-input"
              type="radio"
              name="serviceTypeOptions"
              id="serviceType.recurring"
              value="recurring"
              checked={serviceType.recurring}
              onChange={onServiceTypeChange}
            />
            <label className="form-check-label" htmlFor="serviceType.recurring">
              <span className="service-type-title">Recurring: </span>
              <span className="text-muted">
                Help during all investment cycles. $10,000 service fee for first
                investment cycle. $8,000 fee for each cycle after that.
              </span>
            </label>
          </div>
          <div id="legal-text-container" className="col-sm-12">
            <span className="text-muted text-sm">
              DISCLAIMER: Results shown with this calculator are for
              informational purposes only. The calculations performed within are
              based on approximations derived from past performance and personal
              experience in the tax lien market. Please note that investing
              involves varying degrees of risk, and there can be no assurance
              that the future performance of any specific investment or
              investment strategy will be profitable. Lastly, service fees
              charged by iWealth are subject to change.
            </span>
          </div>
        </div>
      </div>
    );
  }

  render() {
    const { secondaryMarket } = this.props;

    return (
      <React.Fragment>
        <h3 className="text-secondary">Calculate</h3>
        {secondaryMarket
          ? this.renderSecondaryMarket()
          : this.renderLiveAuctionText()}
        <button type="submit" className="btn btn-primary btn-block mt-5">
          Calculate
        </button>
      </React.Fragment>
    );
  }
}

export default CalculateFormPartial;

// *****************************
// Deal Information
// *****************************
// Fee Structure
// --Setup: One-time (Up to 20 liens can purchase for $10,000)
// --Fully Managed: Ongoing (2nd time forward get 10% discount)
// Non Redemption Ratio (e.g. 1 in 6 non-redemptions) --Done
// --Need at least this many liens (need this much cash = avg lien cost * ratio + fee)
// Avg Net Rent (Caculated Field Gross minus costs) -- Done
// --Gross Rent
// --Pct Management (10%)
// --Pct Vacancy
// --Pct Repair
