import React, { Component } from "react";
import Slider, { createSliderWithTooltip } from "rc-slider";
import "rc-slider/assets/index.css";
import "./dealFormPartial.css";

const SliderWithTooltip = createSliderWithTooltip(Slider);

class DealFormPartial extends Component {
  state = {};

  expenseMarks = {
    0: "0%",
    5: "5%",
    10: "10%",
    15: "15%",
    20: "20%",
    25: "25%"
  };

  grossRentMarks = {
    500: "$500",
    1000: "$1,000",
    1500: "$1,500",
    2000: "$2,000",
    2500: "$2,500",
    3000: "$3,000"
  };

  redemptionRatioMarks = {
    1: "1",
    5: "5",
    10: "10",
    15: "15",
    20: "20"
  };

  getExpenseAmt = expense => {
    const { avgGrossRent, expenses } = this.props.dealInformation;
    const amt = Math.round((avgGrossRent * expenses[expense]) / 100);
    return `($${amt})`;
  };

  render() {
    const {
      onExpenseChange,
      onGrossRentChange,
      onRedemptionRatioChange,
      onFullyManagedChange
    } = this.props;
    const {
      isFullyManaged,
      avgGrossRent,
      expenses,
      redemptionRatio
    } = this.props.dealInformation;

    return (
      <React.Fragment>
        <h3 className="text-secondary">Deal Information</h3>
        <div className="row">
          <div className="col-sm-12">
            <h5>Redemptions</h5>
          </div>
        </div>
        <div className="row">
          <div className="slider-container col-sm-6">
            <p className="slider-title">Non-Redemption Ratio</p>
            <SliderWithTooltip
              min={1}
              max={20}
              marks={this.redemptionRatioMarks}
              tipFormatter={v => `  1 in ${v} tax liens becomes a house.  `}
              tipProps={{ overlayClassName: "foo" }}
              value={redemptionRatio}
              onChange={onRedemptionRatioChange}
            />
          </div>
          <div className="col-sm-6">
            <p className="redemption-ratio-text">
              1 in {redemptionRatio} liens becomes a house.
            </p>
          </div>
        </div>

        <div className="row">
          <div className="col-sm-12">
            <h5>Rental Income</h5>
          </div>
        </div>

        <div className="row">
          <div className="col-sm-6">
            <p className="slider-title">Avg. Gross Rent</p>
            <SliderWithTooltip
              min={500}
              max={3000}
              marks={this.grossRentMarks}
              tipFormatter={v => `$${v}`}
              tipProps={{ overlayClassName: "foo" }}
              value={avgGrossRent}
              onChange={v => onGrossRentChange(v)}
            />
          </div>
          <div className="col-sm-6">
            <p className="avg-gross-rent-amount">${avgGrossRent}</p>
          </div>
        </div>

        <div className="row">
          <div className="col-sm-6">
            <p className="slider-title">Management</p>
            <SliderWithTooltip
              min={0}
              max={25}
              marks={this.expenseMarks}
              tipFormatter={v => `${v} %`}
              tipProps={{ overlayClassName: "foo" }}
              value={expenses.management}
              onChange={v => onExpenseChange(v, "management")}
            />
          </div>
          <div className="col-sm-6">
            <p className="expense-cost">{this.getExpenseAmt("management")}</p>
          </div>
        </div>

        <div className="row">
          <div className="col-sm-6">
            <p className="slider-title">Vacancy</p>
            <SliderWithTooltip
              min={0}
              max={25}
              marks={this.expenseMarks}
              tipFormatter={v => `${v} %`}
              tipProps={{ overlayClassName: "foo" }}
              value={expenses.vacancy}
              onChange={v => onExpenseChange(v, "vacancy")}
            />
          </div>
          <div className="col-sm-6">
            <p className="expense-cost">{this.getExpenseAmt("vacancy")}</p>
          </div>
        </div>
        <div className="row">
          <div className="col-sm-6">
            <p className="slider-title">Repair</p>
            <SliderWithTooltip
              min={0}
              max={25}
              marks={this.expenseMarks}
              tipFormatter={v => `${v} %`}
              tipProps={{ overlayClassName: "foo" }}
              value={expenses.repair}
              onChange={v => onExpenseChange(v, "repair")}
            />
          </div>
          <div className="col-sm-6">
            <p className="expense-cost">{this.getExpenseAmt("repair")}</p>
          </div>
        </div>
        <div className="row">
          <p
            className="col-sm-6 net-rent-title"
            style={{ textAlign: "center" }}
          >
            Avg. Net Rent
          </p>
          <p className="col-sm-6 net-rent-amount">
            $
            {Math.round(
              avgGrossRent *
                (1 -
                  (expenses.management + expenses.vacancy + expenses.repair) /
                    100)
            )}
          </p>
        </div>
        <div className="row">
          <div className="col-sm-12">
            <h5>iWealth Service Type</h5>
            <p className="text-muted">
              iWealth offers investors help during the tax lien process. I need
              to get form information on Fully Managed Fee, Recurring Fee,
              one-time fee in order to lay this out better
            </p>
          </div>
          <div className="col-sm-12">
            <div class="form-check">
              <input
                className="form-check-input"
                type="radio"
                name="serviceTypeOptions"
                id="serviceType.setup"
                value="setup"
                checked
              />
              <label className="form-check-label" for="serviceType.setup">
                Setup: One-time fee to help with first cycle
              </label>
            </div>
            <div class="form-check">
              <input
                className="form-check-input"
                type="radio"
                name="serviceTypeOptions"
                id="serviceType.recurring"
                value="recurring"
                checked
              />
              <label className="form-check-label" for="serviceType.recurring">
                Recurring: Help at each cycle.
              </label>
            </div>
            <div class="form-check">
              <input
                className="form-check-input"
                type="radio"
                name="serviceTypeOptions"
                id="serviceType.Setup"
                value="fullyManaged"
                checked
              />
              <label
                className="form-check-label"
                for="serviceType.fullyManaged"
              >
                Fully Managed
              </label>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default DealFormPartial;

// *****************************
// Deal Information
// *****************************
// Fee Structure
// --Setup: One-time (Up to 20 liens can purchase for $10,000)
// --Fully Managed: Ongoing (2nd time forward get 10% discount)
// Non Redemption Ratio (e.g. 1 in 6 non-redemptions) --Done
// --Need at least this many liens (need this much cash = avg lien cost * ratio + fee)
// Avg Net Rent (Caculated Field Gross minus costs) -- Done
// --Gross Rent
// --Pct Management (10%)
// --Pct Vacancy
// --Pct Repair
