import React, { Component } from "react";
import Slider, { createSliderWithTooltip } from "rc-slider";
import "rc-slider/assets/index.css";

class RenoFormPartial extends Component {
  state = {};

  grossRentMarks = {
    500: "$500",
    1000: "$1,000",
    1500: "$1,500",
    2000: "$2,000",
    2500: "$2,500",
    3000: "$3,000"
  };

  render() {
    return (
      <React.Fragment>
        <h3 className="text-secondary">Reno Information</h3>
        <div className="row">
          <div className="col-sm-12">
            <h5>Repair Costs</h5>
          </div>
          <div className="slider-container col-sm-6">
            <p className="slider-title">Non-Redemption Ratio</p>
            <SliderWithTooltip
              min={1}
              max={20}
              marks={this.grossRentMarks}
              tipFormatter={v => `  1 in ${v} tax liens becomes a house.  `}
              // value={redemptionRatio}
              // onChange={onRedemptionRatioChange}
            />
          </div>
          <div className="col-sm-6">
            <p className="redemption-ratio-text">
              1 in {redemptionRatio} liens becomes a house.
            </p>
          </div>
          <div className="col-sm-12">
            <h5>Financing</h5>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default RenoFormPartial;

