import React, { Component } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { getActiveStates } from "./../../services/fakeStateService";

// If i don't end up using this, I need to npm uninstall.
// import Slider from "rc-slider";
// import "rc-slider/assets/index.css";

// const createSliderWithTooltip = Slider.createSliderWithTooltip;
// const Range = createSliderWithTooltip(Slider.Range);
// const Handle = Slider.Handle;

class InvestorFormPartial extends Component {
  state = {
    locationOptions: []
  };

  componentDidMount() {
    this.setState({ locationOptions: getActiveStates() });
  }

  render() {
    const { locationOptions } = this.state;
    const {
      onChange,
      onNetRentPreferenceChange,
      onLocationChange,
      onLienCostChange,
      onSecondaryMarketChange
    } = this.props;
    const {
      secondaryMarket,
      startDate,
      passiveIncomeGoal,
      investmentCapital,
      lienCosts
    } = this.props.investorInformation;
    const {
      paydownDebtPct,
      reinvestPct,
      spendPct
    } = this.props.investorInformation.netRentBreakdown;

    return (
      <React.Fragment>
        <h3 className="text-secondary">Investor Information</h3>

        {/* Income & Capital Group */}
        <div className="row">
          <div className="col-sm-12">
            <h5>Income & Capital</h5>
          </div>

          <div className="form-group col-sm-4">
            <label htmlFor="startDate">Start Date</label>
            <DatePicker
              selected={startDate}
              onChange={onChange}
              className="form-control"
              dateFormat="MM/yyyy"
              showMonthYearPicker
              minDate={new Date()}
            />
          </div>

          <div className="form-group col-sm-4">
            <label htmlFor="passiveIncomeGoal">Passive Income Goal</label>
            <div className="input-group">
              <div className="input-group-prepend">
                <span className="input-group-text">$</span>
              </div>
              <input
                id="passiveIncomeGoal"
                name="passiveIncomeGoal"
                onChange={onChange}
                className="form-control"
                type="number"
                value={passiveIncomeGoal}
              />
            </div>
          </div>

          <div className="form-group col-sm-4">
            <label htmlFor="investmentCapital">Available Capital</label>
            <div className="input-group">
              <div className="input-group-prepend">
                <span className="input-group-text">$</span>
              </div>
              <input
                id="investmentCapital"
                name="investmentCapital"
                onChange={onChange}
                className="form-control"
                type="number"
                value={investmentCapital}
              />
            </div>
          </div>
        </div>

        {/* Net Rent Group */}
        <div className="row">
          <div className="col-sm-12">
            <h5>Plan for Net Rent</h5>
            <p className="text-muted">
              Fix: Adjust handler to fix edge cases where these linked items are
              broken.
            </p>
          </div>

          <div className="form-group col-sm-4">
            <label htmlFor="spendPct">Spend</label>
            <div className="input-group">
              <input
                id="spendPct"
                name="spendPct"
                onChange={onNetRentPreferenceChange}
                className="form-control"
                type="number"
                value={spendPct}
              />
              <div className="input-group-append">
                <span className="input-group-text">%</span>
              </div>
            </div>
          </div>

          <div className="form-group col-sm-4">
            <label htmlFor="paydownDebtPct">Pay Down Debt</label>
            <div className="input-group">
              <input
                id="paydownDebtPct"
                name="paydownDebtPct"
                onChange={onNetRentPreferenceChange}
                className="form-control"
                type="number"
                value={paydownDebtPct}
              />
              <div className="input-group-append">
                <span className="input-group-text">%</span>
              </div>
            </div>
          </div>

          <div className="form-group col-sm-4">
            <label htmlFor="reinvestPct">Reinvest</label>
            <div className="input-group">
              <input
                id="reinvestPct"
                name="reinvestPct"
                onChange={onNetRentPreferenceChange}
                className="form-control"
                type="number"
                value={reinvestPct}
              />
              <div className="input-group-append">
                <span className="input-group-text">%</span>
              </div>
            </div>
          </div>
        </div>

        {/* Investement Location Group */}
        <div className="row">
          <div className="col-sm-12">
            <h5>Investment Locations</h5>
          </div>

          <div className="form-group col-sm-12">
            <div className="custom-control custom-switch">
              <input
                type="checkbox"
                className="custom-control-input"
                id="secondaryMarket"
                name="secondaryMarket"
                // checked="off"
                checked={secondaryMarket}
                onChange={onSecondaryMarketChange}
              />
              <label className="custom-control-label" htmlFor="secondaryMarket">
                Invest in Secondary Market
              </label>
            </div>
          </div>
          <div className="form-group col-4">
            <label htmlFor="location1">State 1</label>
            <select
              onChange={onLocationChange}
              className="form-control"
              name="location1"
              id="location1"
            >
              {locationOptions.map(location => (
                <option
                  key={location.abbreviation}
                  value={JSON.stringify(location)}
                >
                  {location.name}
                </option>
              ))}
            </select>
          </div>
          <div hidden={secondaryMarket} className="form-group col-4">
            <label htmlFor="location1AvgLienCostLiveAuction">
              Avg Lien Cost
            </label>
            <input
              id="location1AvgLienCostLiveAuction"
              name="location1.avgLienCostLiveAuction"
              className="form-control"
              type="number"
              onChange={onLienCostChange}
              value={lienCosts.location1.avgLienCostLiveAuction}
            />
          </div>
          <div hidden={!secondaryMarket} className="form-group col-4">
            <label htmlFor="location1AvgLienCostSecondary">
              Avg Redemptive Value
            </label>
            <input
              id="location1AvgLienCostSecondary"
              name="location1.avgLienCostSecondary"
              className="form-control"
              type="number"
              onChange={onLienCostChange}
              value={lienCosts.location1.avgLienCostSecondary}
            />
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default InvestorFormPartial;

// *****************************
// Investor Information
// *****************************
// (1) Investment Start Date -- Done
// (2) Passive Income Goal -- Done
// (3) Cash Available for Investing -- Done
// (4) Preference for treatment of Rent Money (Net Rent Breakdown) -- Done
// -- (a) Paydown Debt (accelerate payments towards outstanding loans)
// -- (b) Save for more liens (e.g. add to pool of money for buying more tax liens)
// -- (c) Passive Income to enjoy some of it (take money out that will not be used for investing at all)
// (5) States willing to Invest In -- WIP
//  -- (a) First choice
//  -- (b) Second choice
//  -- (c) Third choice

// ---- Will determing Redemption Period
// ---- Will determine Foreclosure Months
// ---- Need some way to prioritize one state over another
