import React, { Component } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { getActiveStates } from "./../../services/fakeStateService";
import Joi from "joi-browser";

class InvestorFormPartial extends Component {
  state = {
    locationOptions: []
  };

  componentDidMount() {
    this.setState({ locationOptions: getActiveStates() });
  }

  getLocationLabel = index => {
    switch (index) {
      case 0:
        return "Primary";
      case 1:
        return "Alternate 1";
      default:
        return "Alternate 2";
    }
  };

  renderError = field => {
    const { errors } = this.props;
    if (!errors) return;
    if (!errors[field]) return;
    return (
      <div>
        <small>{errors[field]}</small>
      </div>
    );
  };

  render() {
    const { locationOptions } = this.state;
    const {
      onDateChange,
      onNumberInputChange,
      onNetRentPreferenceChange,
      onLocationChange,
      onLienCostChange,
      onLienInterestChange,
      onSecondaryMarketChange
    } = this.props;
    const {
      secondaryMarket,
      startDate,
      passiveIncomeGoal,
      investmentCapital,
      lienCosts,
      lienInterestRates,
      locations
    } = this.props.investorInformation;
    const {
      paydownDebtPct,
      reinvestPct,
      spendPct
    } = this.props.investorInformation.netRentBreakdown;

    return (
      <React.Fragment>
        <h3 className="text-secondary">Investor Information</h3>

        {/* Income & Capital Group */}
        <div className="row">
          <div className="col-sm-12">
            <h5>Income & Capital</h5>
          </div>

          <div className="form-group col-sm-4">
            <label htmlFor="startDate">Start Month</label>
            <br />
            <DatePicker
              selected={startDate}
              onChange={onDateChange}
              className="form-control"
              dateFormat="MM/yyyy"
              showMonthYearPicker
            />
          </div>

          <div className="form-group col-sm-4">
            <label htmlFor="passiveIncomeGoal">Passive Income</label>
            <div className="input-group">
              <div className="input-group-prepend">
                <span className="input-group-text">$</span>
              </div>
              <input
                id="passiveIncomeGoal"
                name="investorInformation.passiveIncomeGoal"
                onChange={onNumberInputChange}
                className="form-control"
                type="number"
                value={passiveIncomeGoal}
              />
              {this.renderError("investorInformation.passiveIncomeGoal")}
            </div>
          </div>

          <div className="form-group col-sm-4">
            <label htmlFor="investmentCapital">Available Capital</label>
            <div className="input-group">
              <div className="input-group-prepend">
                <span className="input-group-text">$</span>
              </div>
              <input
                id="investmentCapital"
                name="investorInformation.investmentCapital"
                onChange={onNumberInputChange}
                className="form-control"
                type="number"
                value={investmentCapital}
              />
            </div>
          </div>

          {/* <div className="form-group col-sm-4">
            <label htmlFor="addedCapital">Contributions</label>
            <div className="input-group">
              <div className="input-group-prepend">
                <span className="input-group-text">$</span>
              </div>
              <input
                id="addedCapital"
                name="addedCapital"
                onChange={onChange}
                className="form-control"
                type="number"
                value={investmentCapital}
              />
            </div>
          </div> */}
        </div>

        {/* Net Rent Group */}
        <div className="row">
          <div className="col-sm-12">
            <h5>Plan for Net Rent</h5>
          </div>

          <div className="form-group col-sm-4">
            <label htmlFor="spendPct">Retained Income</label>
            <div className="input-group">
              <input
                id="spendPct"
                name="spendPct"
                onChange={onNetRentPreferenceChange}
                className="form-control"
                type="number"
                value={spendPct}
              />
              <div className="input-group-append">
                <span className="input-group-text">%</span>
              </div>
            </div>
          </div>

          <div className="form-group col-sm-4">
            <label htmlFor="paydownDebtPct">Pay Down Debt</label>
            <div className="input-group">
              <input
                id="paydownDebtPct"
                name="paydownDebtPct"
                onChange={onNetRentPreferenceChange}
                className="form-control"
                type="number"
                value={paydownDebtPct}
              />
              <div className="input-group-append">
                <span className="input-group-text">%</span>
              </div>
            </div>
          </div>

          <div className="form-group col-sm-4">
            <label htmlFor="reinvestPct">Reinvest</label>
            <div className="input-group">
              <input
                id="reinvestPct"
                name="reinvestPct"
                onChange={onNetRentPreferenceChange}
                className="form-control"
                type="number"
                value={reinvestPct}
              />
              <div className="input-group-append">
                <span className="input-group-text">%</span>
              </div>
            </div>
          </div>
        </div>

        {/* Investement Location Group */}
        <div className="row">
          <div className="col-sm-12">
            <h5>Investment Locations</h5>
          </div>

          <div className="form-group col-sm-12">
            <div className="custom-control custom-switch">
              <input
                type="checkbox"
                className="custom-control-input"
                id="secondaryMarket"
                name="secondaryMarket"
                checked={secondaryMarket}
                onChange={onSecondaryMarketChange}
              />
              <label className="custom-control-label" htmlFor="secondaryMarket">
                Invest in Secondary Market
              </label>
            </div>
          </div>

          {/* Location Drop Downs */}
          {locations.map((location, index) => (
            <React.Fragment key={`location${index}`}>
              <div className="form-group col-sm-6">
                <label htmlFor={`location${index}`}>
                  {this.getLocationLabel(index)}
                </label>
                <select
                  onChange={onLocationChange}
                  className="form-control"
                  name={`location-${index}`}
                  id={`location${index}`}
                  value={location.abbreviation}
                >
                  {locationOptions.map(locOption => (
                    <option
                      key={locOption.abbreviation}
                      value={locOption.abbreviation}
                    >
                      {locOption.name}
                    </option>
                  ))}
                </select>
              </div>
              <div hidden={secondaryMarket} className="form-group col-sm-3">
                <label htmlFor={`avgLienCostLiveAuction-${index}`}>
                  Avg Lien Cost
                </label>
                <input
                  id={`avgLienCostLiveAuction-${index}`}
                  name={`avgLienCostLiveAuction-${index}`}
                  className="form-control"
                  type="number"
                  onChange={onLienCostChange}
                  value={lienCosts[index].avgLienCostLiveAuction}
                  disabled={location.abbreviation === "DEFAULT"}
                />
              </div>
              <div hidden={!secondaryMarket} className="form-group col-sm-4">
                <label htmlFor={`avgLienCostSecondary-${index}`}>
                  Avg Redemptive Value
                </label>
                <input
                  id={`avgLienCostSecondary-${index}`}
                  name={`avgLienCostSecondary-${index}`}
                  className="form-control"
                  type="number"
                  onChange={onLienCostChange}
                  value={lienCosts[index].avgLienCostSecondary}
                  disabled={location.abbreviation === "DEFAULT"}
                />
              </div>
              <div className="form-group col-sm-2">
                <label htmlFor={`avgLienInterestRate-${index}`}>Interest</label>
                <input
                  id={`avgLienInterestRate-${index}`}
                  name={`avgLienInterestRate-${index}`}
                  className="form-control"
                  type="number"
                  onChange={onLienInterestChange}
                  value={lienInterestRates[index]}
                  disabled={location.abbreviation === "DEFAULT"}
                />
              </div>
            </React.Fragment>
          ))}
        </div>
      </React.Fragment>
    );
  }
}

