export default class Period {
  constructor(date, investmentCapital) {
    this.date = date;
    this.investmentCapital = investmentCapital;
    this.foreclosureCost = 0;
    this.redemptionIncome = 0;
  }

  setPurchasePlan(plan) {
    this.purchasePlan = plan;
  }

  setRequiedDebtPayment(value) {
    this.requiredDebtPayment = value;
  }

  setTotalLoanBalance(value) {
    this.totalLoanBalance = value;
  }

  setExtraDebtPayment(value) {
    this.extraDebtPayment = value;
  }

  setRentBreakdown(value) {
    this.rentBreakdown = value;
  }

  setPassiveIncome(value) {
    this.passiveIncome = value;
  }

  setRentReinvested(value) {
    this.rentReinvested = value;
  }

  setTotalHouseCount(value) {
    this.totalHouseCount = value;
  }

  updateForeclosureCost(value) {
    this.foreclosureCost += value;
  }

  updateRedemptionIncome(value) {
    this.redemptionIncome += value;
  }

  // investmentCapital = 0;
  // liensPurchased = 0;
  // housesAcquired = 0;
  // outstandingDebt = 0;
  // passiveRentalIncome = 0;
}

// Outputs
// Should be a schedule that can be viewed as years (aggregated values) or months
// Should last 24 Months after Investment Goal is hit
// Phase 2 Consideration --- people can pick fields to add to output.
